import React, { Component } from 'react';

//Styles.
import './../assets/css/Tasks.css';

//Data.
import tasks from './../simulation_data/tasks.json';

class Tasks extends Component{

    state = {
        tasks: tasks
    }

    render(){
        return(
            <div className="tasks">
		        <div className="wrap">
                    <ul className="list" id="lista">

                        {
                            this.state.tasks.map(task => {
                                return(
                                    <div className="tasks">
                                        <div className=""></div>
                                        <p>{task.title}</p>
                                    </div>
                                )
                            })
                        }

                        <li><p>1 This is the task one.</p></li>
                        <li><p>2 This is the task two.</p></li>
                        <li><p>3 This is the task three.</p></li>
                    </ul>
		        </div>
	        </div>
        )
    }
}

export default Tasks;
